<script>
import bus from 'vue3-eventbus'
import { ref } from 'vue'

export default {
  name: 'PreBar',
  setup () {
    const keyWord = ref('')

    function handleSearch () {
      bus.emit('searchByKeyWord', { keyWord: keyWord.value })
    }

    function changePre (mode) {
      bus.emit('changePreMode', mode)
    }

    return {
      keyWord,
      changePre,
      handleSearch
    }
  }
}
</script>

