    const titles = ref([]) // root的children
    const selected = ref([]) // 存储被选中的对象
    const copied = ref([]) // 存储被复制的对象路径
    const store = useStore()
    const TabXY = ref({ x: -1, y: -1 })
    // eslint-disable-next-line no-unused-vars
    const { proxy, ctx } = getCurrentInstance()
    const _this = proxy
    const mode = computed(() => {
      return store.getters.getMode
    })
    const loading = ref(false)

    bus.on('changeLoading', (value) => {
      loading.value = value
    })

    watch(() => store.state.xy, (newValue, oldValue) => {
      const temp = TabXY.value.x + '+' + TabXY.value.y
      if (store.getters.getXY !== temp) {
        _this.$refs.contextmenu.hide()
      }
    })

    const textWords = computed(() => {
      switch (isFile.value) {
        case 0:
          return '资源管理器'
        case 1:
          return '搜索'
        case 2:
          return '文档大纲'
        case 3:
          return '引用管理器'
        case 4:
          return '标签管理器'
        case 5:
          return '榕林'
        case 6:
          return '榕图'
        case 7:
          return '偏好设置'
        default:
          return '文档大纲'
      }
    })

    bus.on('addToSelected', (obj) => {
      selected.value.push(obj)
    })

    bus.on('newSelected', (obj) => {
      selected.value.length = 0
      selected.value.push(obj)
    })

    bus.on('deleteFromSelected', (index) => {
      selected.value.splice(index, 1)
    })

    function handleRightClick (e) {
      TabXY.value = { x: e.clientX, y: e.clientY }
      store.dispatch('updateXY', TabXY.value)
    }

    bus.on('openOutLine', (obj) => {
      titles.value = obj
    })

    bus.on('copyFileOrFolder', (obj) => {
      // 右键一个对象，如果对象已经在selectedList中，则不改变，如果不在，则新建一个selected
      const index = selected.value.findIndex(item => item.path === obj.path)
      if (index === -1) {
        selected.value.length = 0
        selected.value.push(obj)
      }
      copied.value.length = 0
      selected.value.forEach(item => copied.value.push(item.path))
    })

    function handleNew (type) {
      if (props.data.length !== 0) {
        bus.emit('showDialogForNewFile', { type, father: props.data[0] })
      }
    }

    function getTags () {
      isFile.value = 4
      bus.emit('changeToTag')
    }

    function getSearch () {
      if (props.data.length === 0) {
        bus.emit('showMyAlert', { message: '必须要打开文件夹才能使用全局搜索' })
      } else {
        isFile.value = 1
      }
    }

    function getGraph () {
      if (props.data.length === 0) {
        bus.emit('showMyAlert', { message: '必须要打开文件夹才能体验榕图模式' })
      } else {
        isFile.value = 6
        bus.emit('changeToGraph')
      }
    }

    function getForest () {
      if (props.data.length === 0) {
        bus.emit('showMyAlert', { message: '必须要打开文件夹才能体验榕林模式' })
      } else {
        isFile.value = 5
        bus.emit('changeToForest')
      }
    }

    watch(() => store.getters.getMode, (newValue, oldValue) => {
      if (newValue >= 0 && newValue <= 2) {
        if (isFile.value >= 5) {
          isFile.value = 2
        }
      }
    })

    watch(isFile, (newValue, oldValue) => {
      const res = (newValue === 4) ? 1 : 0
      bus.emit('showAllTag', res)
      // if ((oldValue === 6 && newValue !== 7) || (oldValue === 7 && newValue !== 6)) {
      //   bus.emit('backToEditMode')
      // }
      if (oldValue >= 5 && newValue <= 4) {
        bus.emit('backToEditMode')
      }
      if (newValue === 7) {
        // 禁用快捷键
        window.electronAPI.disableAllKeybindings()
      }
      if (oldValue === 7) {
        // 启用快捷键
        window.electronAPI.enableAllKeybindings()
        store.dispatch('SAVE_PREFERENCES')
      }
    })

    bus.on('quitFromGraph', () => {
      isFile.value = 0
      bus.emit('backToEditMode')
    })

    async function flushTree () {
      if (props.data.length !== 0) {
        const projPath = props.data[0].path
        const newChildren = await window.electronAPI.refresh(projPath)
        const openDir = [{
          name: props.data[0].name,
          path: projPath,
          children: newChildren,
          curChild: -1,
          absolutePath: props.data[0].absolutePath,
          offset: -1,
          type: 'folder'
        }]
        bus.emit('openDir', openDir[0])
      }
      // selected.value.length = 0
    }

    async function handleFlush () {
      if (isFile.value === 0) {
        await flushTree()
      } else if (isFile.value === 1) {
        bus.emit('reSearch')
      } else if (isFile.value === 2) {
        bus.emit('updateOutLine')
      } else if (isFile.value === 3) {
        bus.emit('updateCite')
      } else if (isFile.value === 4) {
        bus.emit('updateTag')
      } else if (isFile.value === 5) {
        await flushTree()
        bus.emit('changeToForest')
      } else if (isFile.value === 6) {
        await flushTree()
        bus.emit('changeToGraph')
      }
    }

    function handleClear () {
      if (isFile.value === 1) {
        bus.emit('clearSearchResult')
      } else if (isFile.value === 5) {
        bus.emit('clearForestResult')
      } else if (isFile.value === 6) {
        bus.emit('clearGraphResult')
      }
    }

    async function handlePaste () {
      if (props.data.length !== 0) {
        const projPath = props.data[0].path
        await window.electronAPI.paste(copied.value[0], projPath)
      }
    }

    bus.on('pasteForSpecialPath', async (path) => {
      if (props.data.length !== 0) {
        await window.electronAPI.paste(copied.value[0], path)
      }
    })

    function handleCloseWorkArea () {
      selected.value.length = 0 // 清空选中区
      bus.emit('closeDir')
    }

    function getRelation () {
      isFile.value = 3
      bus.emit('changeToRelation')
    }

    onMounted(() => {
      const sideBar = document.querySelector('.Sidebar')
      let startX, startWidth

      function changeCurSor (e) {
        const leftWidth = sideBar.offsetWidth
        if (e.clientX <= sideBar.offsetLeft + leftWidth + 15 && e.clientX >= sideBar.offsetLeft + leftWidth - 15) {
          sideBar.style.cursor = 'ew-resize'
        } else {
          sideBar.style.cursor = 'default'
        }
      }

      sideBar.addEventListener('mouseover', changeCurSor)
      sideBar.addEventListener('mouseout', changeCurSor)

      sideBar.addEventListener('mousedown', function (e) {
        startX = e.clientX
        const leftWidth = sideBar.offsetWidth
        if (e.clientX <= sideBar.offsetLeft + leftWidth + 50 && e.clientX >= sideBar.offsetLeft + leftWidth - 50) {
          startWidth = parseInt(document.defaultView.getComputedStyle(sideBar).width, 10)
          document.addEventListener('mousemove', mousemove)
          document.addEventListener('mouseup', mouseup)
        }
      })

      function mousemove (e) {
        let newWidth = startWidth + e.clientX - startX
        // const windowWidth = window.innerWidth
        // if (newWidth > windowWidth / 2) {
        //   newWidth = windowWidth / 2
        // }
        if (newWidth > 400) {
          newWidth = 400
        } else if (newWidth < 250) {
          newWidth = 250
        }
        sideBar.style.width = newWidth + 'px'
        store.dispatch('updateSideBarWidth', { value: newWidth })
        // rightChild.style.width = 1152 - newWidth + 'px'
      }

      function mouseup () {
        document.removeEventListener('mousemove', mousemove)
        document.removeEventListener('mouseup', mouseup)
      }
    })

    function handleShowTag (index) {
      bus.emit('showAllTag', index)
    }

    async function getPre () {
      isFile.value = 7
      await store.dispatch('updateMode', { value: 4 })
    }

    bus.on('changeFileValue', (value) => {
      isFile.value = value
    })

    return {
      isFile,
      titles,
      selected,
      textWords,
      mode,
      loading,
      handleRightClick,
      handleNew,
      handlePaste,
      getSearch,
      getTags,
      getGraph,
      getForest,
      handleFlush,
      handleClear,
      getRelation,
      handleCloseWorkArea,
      handleShowTag,
      getPre
    }
  }
}
</script>

